¶翻转卡片

    .container {
        width: 90%;
        max-width: 600px;
        height: auto;
        min-height: 300px;
        margin: 20px auto;
        padding: 20px 10px;
        border: 2px solid #000;
        border-radius: 7px;
        display: flex;
        justify-content: center;
        gap: 15px;
        flex-wrap: wrap;
    }
    .flip-card {
        position: relative;
        width: 200px;
        height: 200px;
        transform-style: preserve-3d;
        perspective: 500px;
        margin: 10px;
    }
    .flip-front, .flip-back {
        position: absolute;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        display: flex;
        justify-content: center;
        align-items: center;
        border-radius: 5px;
        object-fit: cover;
        backface-visibility: hidden;
        transition: transform 0.8s ease-in-out;
        box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    }
    .flip-front img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        border-radius: 5px;
    }
    .flip-back {
        font-size: 2em;
        color: #000;
        padding: 15px;
        background-color: #e2e1e1;
        text-align: center;
    }
    .flip-card:hover .flip-front {
        transform: rotateY(-180deg);
    }
    .flip-card .flip-back {
        transform: rotateY(180deg);
    }
    .flip-card:hover .flip-back {
        transform: rotateY(0deg);
    }
    @media (max-width: 768px) {
        .container {
            width: 95%;
            padding: 10px 5px;
        }
        .flip-card {
            width: 180px;
            height: 180px;
            margin: 10px;
        }
        .flip-back {
            font-size: 1.2em;
            padding: 0px;
        }
    }






