&lt;a onclick=&quot;changeImage(this, &#x27;https://imgapi.xl0408.top/index.php&#x27;)&quot;&gt;    &lt;img src=&quot;https://imgapi.xl0408.top/index.php&quot; alt=&quot;&quot;&gt;&lt;/a&gt;
function changeImage(e, baseUrl) &#123;    const img = e.querySelector(&#x27;img&#x27;);    const separator = baseUrl.includes(&#x27;?&#x27;) ? &#x27;&amp;&#x27; : &#x27;?&#x27;;    img.src = baseUrl + separator + &#x27;t=&#x27; + new Date().getTime();&#125;
